styles:
.farris-treetable {
    position: relative;
}
.resizable-column {
    background-clip: padding-box;
    position: relative;
}
.column-resizer {
    display: block;
    position: absolute!important;
    top: 0;
    right: 0;
    margin: 0;
    width: .5em;
    height: 100%;
    padding: 0;
    cursor: col-resize;
    border: 1px solid transparent;
}
.column-resizer-helper {
    width: 1px;
    position: absolute;
    z-index: 10;
    display: none;
    border-right: 1px dashed #1890ff;
}

.farris-treetable-disabled .farris-treetable-body .table tr{
    opacity: 0.4;
    cursor: not-allowed;
}
.farris-treetable-disabled .table tr.farris-table-selectedrow{
    opacity: 1;
    cursor: default;
}

.custom-checkbox .custom-control-input:indeterminate~.custom-control-label::after {
    background-image: url("data:image/svg+xml,%3csvg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 4 4'%3e%3cpath stroke='%23fff' d='M0 2h4'/%3e%3c/svg%3e");
    background-color: #1890ff;
}
.unselectable-text {
    -webkit-user-select: none;
    -moz-user-select: none;
    -o-user-select: none;
    -ms-user-select: none;
    user-select: none
}
.search-highlight {
    background-color: #FFFF1F;
}
.search-highlight.active{
    background-color: #FF901C;
}



.f-treetable--fixed-columns .treetable-header-cell {
    vertical-align: middle;
}

.f-treetable--fixed-columns .ps__rail-x,
.f-treetable--fixed-columns .ps__rail-y {
    z-index: 3;
}

.f-treetable--fixed-left,
.f-treetable--fixed-right {
    position: sticky !important;
    z-index: 2;
    background-color: #ffffff;
    overflow: inherit !important;
}
.treetable-header-cell.f-treetable--fixed-left,
.treetable-header-cell.f-treetable--fixed-right {
    background-color: #f7f8fb;
}

.f-treetable--fixed-columns
    tr
    th.f-treetable--fixed-left[l-tt-shadow-col]::after,
.f-treetable--fixed-columns td.f-treetable--fixed-left::after,
.f-treetable--fixed-right::before {
    pointer-events: none;
    content: "";
    width: 36px !important;
    display: inline-block !important;
    position: absolute !important;
    margin-top: 0 !important;
    top: 0 !important;
    height: 100% !important;
    bottom: -1px !important;
    transition: box-shadow 0.2s cubic-bezier(0.4, 0, 0.2, 1);
    background-color: transparent !important;
}

.f-treetable--fixed-columns
    tr
    th.f-treetable--fixed-left:not([l-tt-shadow-col])::after {
    content: "";
    position: absolute;
    right: 0;
    top: 50%;
    margin-top: calc(-1.125rem / 2);
    display: block;
    height: 1.125rem;
    width: 1px;
    background-color: #e6e9f0;
    box-shadow: none;
}

.f-treetable--fixed-columns
    tr
    th.f-treetable--fixed-left[l-tt-shadow-col]::after,
.f-treetable--fixed-columns tr td.f-treetable--fixed-left::after {
    right: -36px !important;
}

.f-treetable--fixed-columns .f-treetable--fixed-right::before {
    left: -36px !important;
}

.f-treetable--fixed-columns .table-hover tbody tr:hover td {
    background-color: #edf5ff;
}

.f-treetable--fixed-columns
    .farris-treetable-tbody
    tr.farris-table-selectedrow
    td {
    background-color: #dae9ff;
    color: #424347;
}

.f-treetable--fixed-columns
    .farris-treetable-tbody
    tr.f-treetable-disable-row
    td {
    background: #fafafc;
}

.f-treetable--fixed-columns.table-bordered
    tr
    th.f-treetable--fixed-left[l-tt-shadow-col],
.f-treetable--fixed-columns.table-bordered tr th.f-treetable--fixed-right,
.f-treetable--fixed-columns.table-bordered tr td.f-treetable--fixed-left,
.f-treetable--fixed-columns.table-bordered tr td.f-treetable--fixed-right {
    border-right: 0;
    box-shadow: inset -1px 0px 0px #eaecf3;
}

.f-treetable--fixed-columns.table-bordered tr td,
.f-treetable--fixed-columns.table-bordered tr td {
    border-left: 0;
}

.f-find-input {
    width: 300px; height: 40px; background: #fefefe; position: absolute; right: 0; z-index: 10; padding: 5px;
    border: 1px solid #d5d5d5;
    box-shadow: 0px 1px 2px 0px #989898;
}
.f-find-input .input-group-text { border-right: 1px solid #ccc; font-size: 10px }
.f-find-input .input-group-append { background: #fff; }
.f-find-input .input-group-append button { padding:1px 6px;}

.treetable-nodes-pagerbar { height: 22px; line-height: 22px; list-style: none}
.treetable-nodes-pagerbar li { float: left; box-sizing: border-box; }
.treetable-nodes-pagerbar .pager-btn {
    width: 22px;
    height: 22px;
    text-align: center;
    border: 1px solid #ccc;
    background: rgb(248 249 250);
    cursor: pointer;
}
.treetable-nodes-pagerbar .pager-btn:hover {
    background: white;
}
.treetable-nodes-pagerbar .prev {}
.treetable-nodes-pagerbar .next {
    border-left: 0;
}

.pager-btn.disabled{
    color: rgb(204 204 204);
    pointer-events: none;
}
.treetable-nodes-pagerbar .pageinfo {
    margin-right: 5px;
    padding-left: 0;
    font-weight: 400;
    padding-right: 5px;
}

.treetable-nodes-pagerbar .page-index {
    border: 0;
    background: transparent;
    outline: none;
    appearance: none;
    padding-left: 5px;
    min-width: 50px;
    position: absolute;
    height: 20px;
    text-align: left;
    opacity: 0;
    left: 0;
}

.treetable-nodes-pagerbar .page-number-list {
    height: 20px;
    min-width: 30px;
    max-width: 50px;
    overflow: hidden;
    text-align: center;
    position: relative;
    border-bottom: 1px solid #ced4da;
}

.loading{
    width: 150px;
    height: 10px;
    margin: 0 auto;
    margin-top:10px;
    margin-bottom: 20px;
}
.loading span{
    display: inline-block;
    width: 10px;
    height: 100%;
    margin-right: 5px;
    border-radius: 50%;
    background: #59a1ff;
    -webkit-animation: load 1.04s ease infinite;
}
.loading span:last-child{
    margin-right: 0px;
}
@-webkit-keyframes load{
    0%{
        opacity: 1;
        -webkit-transform: scale(1.3);
    }
    100%{
        opacity: 0.2;
        -webkit-transform: scale(.3);
    }
}
.loading span:nth-child(1){
    -webkit-animation-delay:0.13s;
}
.loading span:nth-child(2){
    -webkit-animation-delay:0.26s;
}
.loading span:nth-child(3){
    -webkit-animation-delay:0.39s;
}
.loading span:nth-child(4){
    -webkit-animation-delay:0.52s;
}
.loading span:nth-child(5){
    -webkit-animation-delay:0.65s;
}
